(()=>{"use strict";(e=>{const t=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),m=document.getElementById("timer-seconds"),s=()=>{let e=(new Date("18 november 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,days:Math.floor(e/60/60/24),hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}};s().timeRemaining>0&&setInterval((()=>{let e=s();const i=e=>e<=9?"0"+e:e;t.textContent=i(e.days),n.textContent=i(e.hours),o.textContent=i(e.minutes),m.textContent=i(e.seconds)}),1e3)})()})();